import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const [cell1, setCell1] = useState("empty");
  const [cell2, setCell2] = useState("empty");
  const [cell3, setCell3] = useState("empty");
  const [cell4, setCell4] = useState("empty");
  const [cell5, setCell5] = useState("empty");
  const [cell6, setCell6] = useState("empty");
  const [cell7, setCell7] = useState("empty");
  const [cell8, setCell8] = useState("empty");
  const [cell9, setCell9] = useState("empty");

  const [bias, setBias] = useState("Not specified");

  const handleCell = (cell, setCell, index, player) => {
      if (cell == "empty") {
        setCell("X");
        console.log(`Cell ${index} is now occupied by X`);
      } else {
        alert("This cell has already been occupied!");
      }
  }

  const pickPlayer = (choice) => {
    if (choice == "X") {
      setBias("X");
    } else {
      setBias("O");
    }
  }

  return (
    <div className='App'>

      <header className="App-header">
        <h1>Tic-Tac-Toe Game</h1>
      </header>

      <div>You have picked {bias}</div>

      <table>
        <tbody>
          <tr>
            <td><button onClick={() => handleCell(cell1, setCell1, 1)}>{cell1}</button></td>
            <td><button onClick={() => handleCell(cell2, setCell2, 2)}>{cell2}</button></td>
            <td><button onClick={() => handleCell(cell3, setCell3, 3)}>{cell3}</button></td>
          </tr>

          <tr>
            <td><button onClick={() => handleCell(cell4, setCell4, 4)}>{cell4}</button></td>
            <td><button onClick={() => handleCell(cell5, setCell5, 5)}>{cell5}</button></td>
            <td><button onClick={() => handleCell(cell6, setCell6, 6)}>{cell6}</button></td>
          </tr>

          <tr>
            <td><button onClick={() => handleCell(cell7, setCell7, 7)}>{cell7}</button></td>
            <td><button onClick={() => handleCell(cell8, setCell8, 8)}>{cell8}</button></td>
            <td><button onClick={() => handleCell(cell9, setCell9, 9)}>{cell9}</button></td>
          </tr>
        </tbody>
      </table>

      <div>
        {/* <button onClick={setBias("X")}>Pick X</button>
        <button onClick={setBias("O")}>Pick O</button> */}
      </div>

    </div>
  );
}

export default App;
