import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  const [cell1, setCell1] = useState("empty");
  const [cell2, setCell2] = useState("empty");
  const [cell3, setCell3] = useState("empty");
  const [cell4, setCell4] = useState("empty");
  const [cell5, setCell5] = useState("empty");
  const [cell6, setCell6] = useState("empty");
  const [cell7, setCell7] = useState("empty");
  const [cell8, setCell8] = useState("empty");
  const [cell9, setCell9] = useState("empty");


  const handleCell = (cell, setCell, index) => {
    if (cell == "empty") {
      setCell("full");
      console.log(`Cell ${index} is now occupied!`);
    } else {
      alert("This cell has already been occupied!");
    }
  }

  return (
    <div className='App'>

      <header className="App-header">
        <h1>Tic-Tac-Toe Game</h1>
      </header>

      <div>Sina</div>

      <table>
        <tbody>
          <tr>
            <td><button onClick={() => handleCell(cell1, setCell1, 1)}></button>{cell1}</td>
            <td><button onClick={() => handleCell(cell2, setCell2, 2)}></button>{cell2}</td>
            <td><button onClick={() => handleCell(cell3, setCell3, 3)}></button>{cell3}</td>
          </tr>

          <tr>
            <td><button onClick={() => handleCell(cell4, setCell4, 4)}></button>{cell4}</td>
            <td><button onClick={() => handleCell(cell5, setCell5, 5)}></button>{cell5}</td>
            <td><button onClick={() => handleCell(cell6, setCell6, 6)}></button>{cell6}</td>
          </tr>

          <tr>
            <td><button onClick={() => handleCell(cell7, setCell7, 7)}></button>{cell7}</td>
            <td><button onClick={() => handleCell(cell8, setCell8, 8)}></button>{cell8}</td>
            <td><button onClick={() => handleCell(cell9, setCell9, 9)}></button>{cell9}</td>
          </tr>
        </tbody>
      </table>

      <button value="sina"></button>
    </div>
  );
}

export default App;
